    public static HttpServer createServer(int port) throws IOException {
        HttpServer server = HttpServer.create(new InetSocketAddress(port), 0);
        server.createContext("/", new GreetingHandler());
        server.createContext("/insecure", new InsecureHandler());
        server.createContext("/admin", new AdminHandler());
        server.createContext("/error", new ErrorDisclosureHandler());
        server.setExecutor(null); // use the default executor
        return server;
    }

    public static void main(String[] args) throws Exception {
        int port = args.length > 0 ? Integer.parseInt(args[0]) : 7070;
        HttpServer server = createServer(port);
        Runtime.getRuntime().addShutdownHook(new Thread(() -> server.stop(0)));

        server.start();
        System.out.printf("Server running at http://localhost:%d/\n", port);

        // Keep the main thread alive so the server stays up until interrupted.
        Thread.currentThread().join();
    }

    private static class GreetingHandler implements HttpHandler {
        @Override
        public void handle(HttpExchange exchange) throws IOException {
            byte[] response = new App().getGreeting().getBytes(StandardCharsets.UTF_8);
            exchange.getResponseHeaders().set("Content-Type", "text/plain; charset=utf-8");
            exchange.sendResponseHeaders(200, response.length);
            try (OutputStream os = exchange.getResponseBody()) {
                os.write(response);
            }
        }
    }

    // VULNERABILITY: Insecure endpoint with NO security headers, reflected XSS, and no input validation
    private static class InsecureHandler implements HttpHandler {
        @Override
        public void handle(HttpExchange exchange) throws IOException {
            // Get query parameter without any validation or sanitization
            String query = exchange.getRequestURI().getQuery();
            String userName = "Guest";
            
            if (query != null && query.contains("name=")) {
                // Extract name parameter without sanitization - XSS vulnerability
                userName = query.substring(query.indexOf("name=") + 5);
                if (userName.contains("&")) {
                    userName = userName.substring(0, userName.indexOf("&"));
                }
            }
            
            // NO SECURITY HEADERS - ZAP will flag these issues:
            // - Missing X-Frame-Options (clickjacking protection)
            // - Missing X-Content-Type-Options (MIME sniffing protection)
            // - Missing Content-Security-Policy
            // - Missing X-XSS-Protection
            // - Missing Strict-Transport-Security (HSTS)
            
            // Reflected XSS vulnerability - user input directly in HTML response
            String htmlResponse = "<!DOCTYPE html><html><head><title>Insecure Page</title></head>" +
                    "<body>" +
                    "<h1>Welcome " + userName + "!</h1>" +  // User input reflected without encoding
                    "<p>This page has no security configuration.</p>" +
                    "<form>" +
                    "<input type='text' name='name' placeholder='Enter your name'>" +
                    "<button type='submit'>Submit</button>" +
                    "</form>" +
                    "</body></html>";
            
            byte[] response = htmlResponse.getBytes(StandardCharsets.UTF_8);
            
            // Only setting Content-Type, NO security headers
            exchange.getResponseHeaders().set("Content-Type", "text/html; charset=utf-8");
            
            exchange.sendResponseHeaders(200, response.length);
            try (OutputStream os = exchange.getResponseBody()) {
                os.write(response);
            }
        }
    }

    // VULNERABILITY: Cookie Poisoning - Insecure session management
    // The application trusts client-side cookie values without cryptographic signing or server-side validation
    private static class AdminHandler implements HttpHandler {
        @Override
        public void handle(HttpExchange exchange) throws IOException {
            // Get cookies from request
            String cookieHeader = exchange.getRequestHeaders().getFirst("Cookie");
            String userRole = "member"; // default role
            
            // Parse cookie to get user role
            if (cookieHeader != null && cookieHeader.contains("user=")) {
                int start = cookieHeader.indexOf("user=") + 5;
                int end = cookieHeader.indexOf(";", start);
                if (end == -1) end = cookieHeader.length();
                userRole = cookieHeader.substring(start, end);
            }
            
            String htmlResponse;
            
            // VULNERABILITY: Trusting client-side cookie without validation
            // Attacker can change cookie from user=member to user=admin
            if ("admin".equals(userRole)) {
                htmlResponse = "<!DOCTYPE html><html><head><title>Admin Panel</title></head>" +
                        "<body>" +
                        "<h1>üîì You are the admin!</h1>" +
                        "<p>Welcome to the admin panel. You have full access.</p>" +
                        "<p>Current cookie: user=" + userRole + "</p>" +
                        "</body></html>";
            } else {
                htmlResponse = "<!DOCTYPE html><html><head><title>Access Denied</title></head>" +
                        "<body>" +
                        "<h1>‚ùå Access Denied</h1>" +
                        "<p>You are not authorized to access this page.</p>" +
                        "<p>Current cookie: user=" + userRole + "</p>" +
                        "<p>Only admins can access this page.</p>" +
                        "</body></html>";
            }
            
            byte[] response = htmlResponse.getBytes(StandardCharsets.UTF_8);
            
            // Set default cookie if not present
            if (cookieHeader == null || !cookieHeader.contains("user=")) {
                exchange.getResponseHeaders().set("Set-Cookie", "user=member; Path=/");
            }
            
            exchange.getResponseHeaders().set("Content-Type", "text/html; charset=utf-8");
            exchange.sendResponseHeaders(200, response.length);
            try (OutputStream os = exchange.getResponseBody()) {
                os.write(response);
            }
        }
    }

    // VULNERABILITY 4: Application Error Disclosure
